<template>
  <div class="jumbotron bg-transparent">
    
        <div class="container">
            <div class="row">
                <div class="col-sm-6 offset-sm-3">
                  
    <div style="color:#F9E5C5">

        <h2 style="font-size:30px;margin-top:-50px;color: rgba(0,63,71,1);border-color: rgba(2, 44, 49, 0.2);"> {{ $t('message.log') }}</h2>
        <br>
        <form @submit.prevent="handleSubmit" style=" font-size:15px">
            <div class="form-group">
                <label for="username">{{ $t('message.username') }}</label>
                <input type="text" v-model="username" name="username" class="form-control" :class="{ 'is-invalid': submitted && !username }" />
                <div v-show="submitted && !username" class="invalid-feedback">Username is required</div>
            </div>
            <div class="form-group">
                <label htmlFor="password">{{ $t('message.password') }}</label>
                <input type="password" v-model="password" name="password" class="form-control" :class="{ 'is-invalid': submitted && !password }" />
                <div v-show="submitted && !password" class="invalid-feedback">Password is required</div>
            </div>
            <div class="form-group">
                <button class="btn btn-info" :disabled="status.loggingIn">{{ $t('message.log') }}</button>
                <img v-show="status.loggingIn" src="data:image/gif;base64," />
               
            
                  <router-link to="/home" class="btn btn-info">{{ $t('message.cancel') }}</router-link>
                  
            </div>
        </form>
    </div>
     </div>
      </div>
       </div>
        </div>
    
</template>

<script>
import { mapState, mapActions } from 'vuex'

export default {
    data () {
        return {
            username: '',
            password: '',
            submitted: false
        }
    },
    computed: {
        ...mapState('account', ['status'])
    },
    created () {
        // reset login status
        this.logout();
    },
    methods: {
        ...mapActions('account', ['login', 'logout']),
        handleSubmit (e) {
            this.submitted = true;
            const { username, password } = this;
            if (username && password) {
                this.login({ username, password })
            }
        }
    }
};
</script>